package corejava;

public class ForEach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String [] ary= {"A","B","C"};
		int i=0;
		for(String s:ary) {
			i++;
			if(s=="C") 
		System.out.println(i+"."+s);
			System.out.println(s);
		
			
			
	}
  }

}
